sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();

	// Query the database for NeedIt Task records with Due date field values older
	// then the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var overdueNITasks1 = new GlideRecord('x_228566_needit_needit_task_1s');
	overdueNITasks1.addQuery('due_date','<=',rightNow);
	overdueNITasks1.addQuery('state','!=',3);

	overdueNITasks1.query();
	// Write a log message for each overdue NeedIt Task Record
	while(overdueNITasks1.next()){
		gs.info("Overdue NeedIt Task record = " + overdueNITasks1.number);
		gs.eventQueue('x_228566_needit.overdueNeedItTask1',overdueNITasks1,overdueNITasks1.number,gs.getUserName());
	}
